        [TestCase("TempA", "CDX_TempA", TestName = "Deleting index CDX_TempA")]
        [TestCase("TempA", "NIDX_TempA_Report", TestName = "Deleting index NIDX_TempA_Report")]
        [TestCase("TempA", "NCCI_TempA_Report", TestName = "Deleting index NCCI_TempA_Report")]
        [TestCase("TempB", "CCI_TempB_Report", TestName = "Deleting index CCI_TempB_Report")]
        public void DeleteIndexShouldDetectMissingIndex(string tableName, string indexName)
        {
            this.dataDrivenIndexTestHelper.CreateIndex(indexName);

            sqlHelper.Execute($"DROP INDEX IF EXISTS {indexName} on dbo.{tableName}", 120);

            var indexRow = this.expectedIndexViews.Find(x => x.IndexName == indexName);

            indexRow.IsIndexMissing = true;
            indexRow.IndexUpdateType = "CreateMissing";

            this.AssertIndexViews(tableName, indexName);

            this.dataDrivenIndexTestHelper.ExecuteSPRefreshIndexStructuresQueue(true, true);
            this.dataDrivenIndexTestHelper.ExecuteSPRefreshIndexStructuresRun(true, "dbo", tableName);
            
            indexRow.IsIndexMissing = false;
            indexRow.IndexUpdateType = "None";
            indexRow.IsOnlineOperation = false;
            
            this.AssertIndexViews(tableName, indexName);
        }

