        public void IndexUpdateStrategyChoice_Tests(string tableName, string indexName, string optionUpdateList, string updateType, string listOfChanges)
        {
            this.dataDrivenIndexTestHelper.CreateIndex(indexName);

            if (optionUpdateList != null)
            {
                sqlHelper.Execute($"UPDATE Utility.IndexesRowStore SET {optionUpdateList} WHERE SchemaName = 'dbo' AND TableName = '{tableName}' AND IndexName = '{indexName}'", 120); 
            }

            var indexRow = this.dataDrivenIndexTestHelper.GetIndexViews(tableName).Find(x => x.IndexName == indexName);
            Assert.AreEqual(updateType, indexRow.IndexUpdateType, "indexUpdateType");
            Assert.AreEqual(listOfChanges, indexRow.ListOfChanges, "listOfChanges");
        }

        private void AssertIndexViews(string tableName, string indexName = null)
        {
            var result = this.dataDrivenIndexTestHelper.GetIndexViews(tableName);

            expectedIndexViews = expectedIndexViews.FindAll(view => view.TableName == tableName);

            if (!string.IsNullOrEmpty(indexName))
            {
                result = result.FindAll(view => view.IndexName == indexName);
                expectedIndexViews = expectedIndexViews.FindAll(view => view.IndexName == indexName);

                Assert.IsNotEmpty(result);
                Assert.IsNotEmpty(expectedIndexViews);
            }

            Assert.AreEqual(expectedIndexViews.Count, result.Count, "Number of indexes.");

            foreach (var expectedIndexModel in expectedIndexViews)
            {
                var actualIndexModel = result.Find(x => x.IndexName == expectedIndexModel.IndexName);
                this.AssertSpecificIndexViews(expectedIndexModel, actualIndexModel);
            }
        }

        private void AssertForeignKeys()
        {
            var actualForeignKeys = this.dataDrivenIndexTestHelper.GetForeignKeys(TempTableName);
            var existingForeignKeyNames = this.dataDrivenIndexTestHelper.GetExistingForeignKeyNames(TempTableName);

            Assert.AreEqual(this.expectedForeignKeys.Count, actualForeignKeys.Count);

            foreach (var expectedForeignKey in this.expectedForeignKeys)
            {
                var actualForeignKey = actualForeignKeys.Find(f => f.FKName == expectedForeignKey.FKName);

                Assert.NotNull(actualForeignKey, "actualForeignKey");

                Assert.AreEqual(expectedForeignKey.FKName, actualForeignKey.FKName, "FK FKName");
