(
	SELECT	V.DatabaseName,
			V.SchemaName,
            V.TableName,
            DriveLetter, 
            @FileType AS FileType,
		    --SUM(V.UsedSpaceMB) AS UsedSpaceMB,
            ISNULL(CASE @FileType
