        CROSS JOIN (SELECT CAST(SettingValue AS INT) AS FreeSpaceMultiplier 
                    FROM DOI.DOISettings 
                    WHERE SettingName = 'FreeSpaceCheckerTestMultiplierForTempDBFiles') SST
    WHERE V.DatabaseName = @DatabaseName
