                                                END
                                                ELSE
                                                BEGIN
                            EXEC DOI.spPrintOutLongSQL
                                        @SQLInput = @SQLBatch,
                                        @VariableName = '@SQLBatch',
                                        @Debug = 0
                                                END
                        PRINT '--Length of SQL Batch ' + CAST(LEN(@SQLBatch) AS NVARCHAR(20))
                        PRINT '--@CurrentPosition ' +  CAST(@CurrentPosition AS NVARCHAR(20))
                END
                ELSE
                BEGIN
                        EXEC dbo.sp_ExecuteSQL @SQLBatch
                END
        END
